string[] searchFiles =
	fileNamesInput.Split(',').Select(f => f.Trim()).ToArray();

if (Directory.Exists(sourcePath) && Directory.Exists(destinationPath))
{
	string[] allFiles = Directory.GetFiles(sourcePath);
	
	foreach (string searchFile in searchFiles)
	{
		var matchingFiles = allFiles.Where(f => Path.GetFileName(f).Contains(searchFile));
		
		foreach (string file in matchingFiles)
		{
			string fileName = Path.GetFileName(file);
			string newFileName = $"{prefix}_{fileName}";
			string destFile = Path.Combine(destinationPath, newFileName);
			
			File.Copy(file, destFile, true);
			Console.WriteLine($"Copied and renamed: {fileName} -> {newFileName}");
		}
	}
	
	Console.WriteLine("Files copied successfully with new names!");
}
else
{
	Console.WriteLine("One or both directories do not exist!");
}